CREATE TABLE IF NOT EXISTS subscriptions (id INT AUTO_INCREMENT PRIMARY KEY, user_id INT NOT NULL, plan_name ENUM('standard', 'premium', 'ultra') NOT NULL, amount DECIMAL(10, 2) NOT NULL, currency VARCHAR(10) DEFAULT 'USDT', transaction_hash VARCHAR(66) NOT NULL UNIQUE, status ENUM('pending', 'active', 'expired') DEFAULT 'pending', start_date DATETIME DEFAULT CURRENT_TIMESTAMP, FOREIGN KEY (user_id) REFERENCES users(id) ON DELETE CASCADE);
